import React from 'react';
import {
  Box,
  Typography,
  Button,
  AppBar,
  Toolbar,
  Container,
  CssBaseline, // Dùng để thiết lập baseline CSS
} from '@mui/material';
import { Link as RouterLink } from 'react-router-dom'; // Dùng RouterLink để chuyển hướng

const HomePage = () => {
  return (
    // CssBaseline giúp thiết lập CSS cơ bản cho ứng dụng MUI
    <CssBaseline>
      <Box 
        sx={{ 
          display: 'flex', 
          flexDirection: 'column', 
          minHeight: '100vh', // Đảm bảo trang chiếm toàn bộ chiều cao màn hình
          backgroundColor: '#f5f5f5', // Màu nền nhẹ cho toàn bộ trang
        }}
      >
        {/* === Header (Vị trí 1, 2, 3) === */}
        <AppBar position="static" color="default" elevation={0} sx={{ borderBottom: (t) => `1px solid ${t.palette.divider}` }}>
          <Toolbar sx={{ justifyContent: 'space-between' }}>
            {/* 1. LOGO */}
            <Typography
              variant="h6"
              color="primary"
              noWrap
              sx={{ fontWeight: 'bold' }}
            >
              LOGO
            </Typography>

            {/* Các nút hành động (2 & 3) */}
            <Box sx={{ display: 'flex', gap: 1 }}>
              {/* 2. ログイン (Đăng nhập) */}
              <Button
                component={RouterLink}
                to="/login"
                variant="outlined"
                sx={{ minWidth: 100 }}
              >
                ログイン
              </Button>
              {/* 3. レジスター (Đăng ký) */}
              <Button
                component={RouterLink}
                to="/register"
                variant="contained"
                color="primary"
                sx={{ minWidth: 100 }}
              >
                レジスター
              </Button>
            </Box>
          </Toolbar>
        </AppBar>

        {/* === Main Content (Vị trí 4) === */}
        {/* Container giữ nội dung chính ở giữa */}
        <Container component="main" sx={{ flexGrow: 1, py: 8, textAlign: 'center' }}>
          {/* 4. プログラム名 (Tên chương trình) */}
          <Typography
            variant="h2"
            component="h1"
            gutterBottom
            sx={{ fontWeight: 'bold', color: '#333' }}
          >
            プログラム名
          </Typography>

          {/* Giới thiệu chương trình */}
          <Typography variant="h6" color="text.secondary" paragraph sx={{ mt: 2 }}>
            プログラム紹介
          </Typography>
          <Typography variant="body1" color="text.secondary" maxWidth="md" sx={{ mx: 'auto' }}>
            ここは、あなたのプログラムやウェブサイトに関する魅力的な紹介文、 tính năng nổi bật, và lời mời gọi đăng ký sẽ được hiển thị. 
            このウェブサイトでできることを訪問者に明確に伝えましょう。
          </Typography>
        </Container>

        {/* === Footer (Vị trí 5) === */}
        <Box
          component="footer"
          sx={{
            py: 3,
            px: 2,
            mt: 'auto', // Đẩy Footer xuống cuối trang
            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[200] : t.palette.grey[800],
            borderTop: (t) => `1px solid ${t.palette.divider}`,
          }}
        >
          <Container maxWidth="lg" sx={{ textAlign: 'center' }}>
            {/* 5. フッター (Footer) */}
            <Typography variant="body2" color="text.secondary">
              フッター © {new Date().getFullYear()} プログラム名. All rights reserved. | プライバシーポリシー
            </Typography>
          </Container>
        </Box>
      </Box>
    </CssBaseline>
  );
};

export default HomePage;
