import React, { useState } from 'react';
import { Box, Button, Typography, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import { Wheel } from 'react-custom-roulette'; // üëà Thay ƒë·ªïi import
import FastfoodIcon from '@mui/icons-material/Fastfood'; // Icon cho v√≤ng quay

// Danh s√°ch c√°c m√≥n ƒÉn ƒë·ªÉ quay
const FOOD_OPTIONS = [
    'Ph·ªü B√≤', 'B√∫n Ch·∫£', 'Nem R√°n', 'B√°nh M√¨', 'G·ªèi Cu·ªën', 
    'C∆°m T·∫•m', 'M√¨ Qu·∫£ng', 'H·ªß Ti·∫øu', 'B√∫n B√≤ Hu·∫ø', 'Ch√°o L√≤ng'
];

const SpinWheel = ({ onFoodSelected }) => {
    const [openDialog, setOpenDialog] = useState(false);
    const [mustSpin, setMustSpin] = useState(false); // State ƒë·ªÉ k√≠ch ho·∫°t v√≤ng quay
    const [prizeNumber, setPrizeNumber] = useState(0); // Index c·ªßa m√≥n ƒÉn tr√∫ng th∆∞·ªüng
    const [finalPrize, setFinalPrize] = useState(''); // T√™n m√≥n ƒÉn cu·ªëi c√πng

    // X·ª≠ l√Ω khi v√≤ng quay d·ª´ng l·∫°i
    const handleSpinStop = () => {
        const selectedPrize = FOOD_OPTIONS[prizeNumber];
        setFinalPrize(selectedPrize);
        setOpenDialog(true); // M·ªü dialog hi·ªÉn th·ªã k·∫øt qu·∫£
        setMustSpin(false); // D·ª´ng tr·∫°ng th√°i quay
        if (onFoodSelected) {
            onFoodSelected(selectedPrize);
        }
    };

    // B·∫Øt ƒë·∫ßu quay
    const handleSpin = () => {
        const randomIndex = Math.floor(Math.random() * FOOD_OPTIONS.length);
        setPrizeNumber(randomIndex);
        setMustSpin(true); // K√≠ch ho·∫°t v√≤ng quay
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setFinalPrize('');
    };

    return (
        <Box sx={{ 
            position: 'fixed', // V·ªã tr√≠ c·ªë ƒë·ªãnh tr√™n m√†n h√¨nh
            bottom: 30,        // C√°ch ƒë√°y 30px
            right: 30,         // C√°ch ph·∫£i 30px
            zIndex: 1000,      // ƒê·∫£m b·∫£o n·∫±m tr√™n c√°c ph·∫ßn t·ª≠ kh√°c
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
        }}>
            {/* N√∫t ƒë·ªÉ k√≠ch ho·∫°t v√≤ng quay (·∫©n/hi·ªán v√≤ng quay) */}
            <Button
                variant="contained"
                color="primary"
                size="large"
                sx={{
                    borderRadius: '50%', // L√†m cho n√∫t tr√≤n
                    width: 80,
                    height: 80,
                    minWidth: 0, // Ghi ƒë√® min-width m·∫∑c ƒë·ªãnh
                    boxShadow: 6,
                    mb: 1, // Kho·∫£ng c√°ch v·ªõi v√≤ng quay (n·∫øu c√≥)
                    // Animation nh·∫π khi hover
                    '&:hover': {
                        transform: 'scale(1.05)',
                        transition: 'transform 0.2s ease-in-out',
                    }
                }}
                onClick={handleSpin}
                aria-label="Quay ƒë·ªÉ ch·ªçn m√≥n ƒÉn"
            >
                <FastfoodIcon sx={{ fontSize: 40 }} />
            </Button>
            <Typography variant="caption" sx={{ color: 'text.secondary', fontWeight: 'bold' }}>Quay M√≥n!</Typography>
            
            {/* V√≤ng quay (ch·ªâ hi·ªÉn th·ªã khi quay) */}
            {/* L∆∞u √Ω: react-wheel-of-prizes c√≥ th·ªÉ hi·ªÉn th·ªã tr·ª±c ti·∫øp.
             N·∫øu b·∫°n mu·ªën n√≥ hi·ªán khi quay v√† ·∫©n sau ƒë√≥, b·∫°n s·∫Ω c·∫ßn m·ªôt state ƒë·ªÉ qu·∫£n l√Ω s·ª± hi·ªÉn th·ªã c·ªßa Wheel.
             ƒê·ªÉ ƒë∆°n gi·∫£n, t√¥i s·∫Ω gi·∫£ ƒë·ªãnh n√≥ ƒë∆∞·ª£c k√≠ch ho·∫°t qua `handleSpin` v√† hi·ªÉn th·ªã k·∫øt qu·∫£ qua dialog.
             Th∆∞ vi·ªán n√†y t·ª± ƒë·ªông k√≠ch ho·∫°t animation khi `prizeNumber` thay ƒë·ªïi. */}
            <Box sx={{ display: 'none' }}> {/* ·∫®n v√≤ng quay kh·ªèi giao di·ªán, ch·ªâ d√πng ƒë·ªÉ render logic */}
                 <Wheel
                    mustStartSpinning={mustSpin}
                    prizeNumber={prizeNumber}
                    data={FOOD_OPTIONS.map(food => ({ option: food }))}
                    onStopSpinning={handleSpinStop}
                    // C√°c props style c√≥ th·ªÉ kh√°c, b·∫°n c√≥ th·ªÉ t√πy ch·ªânh l·∫°i theo t√†i li·ªáu c·ªßa react-custom-roulette
                    // V√≠ d·ª• m·ªôt v√†i props style c∆° b·∫£n:
                    outerBorderColor={"#ccc"}
                    outerBorderWidth={5}
                    innerBorderColor={"#f2f2f2"}
                    radiusLineColor={"#dedede"}
                    radiusLineWidth={1}
                    textColors={["#ffffff"]}
                    backgroundColors={[
                        '#FF6347', '#FFD700', '#ADFF2F', '#87CEEB', '#BA55D3', 
                        '#FF4500', '#DAA520', '#3CB371', '#4169E1', '#9370DB'
                    ]}
                />
            </Box>


            {/* Dialog hi·ªÉn th·ªã k·∫øt qu·∫£ */}
            <Dialog open={openDialog} onClose={handleCloseDialog}>
                <DialogTitle>Ch√∫c m·ª´ng!</DialogTitle>
                <DialogContent>
                    <Typography variant="h5" align="center" sx={{ my: 2 }}>
                        M√≥n ƒÉn ƒë∆∞·ª£c ch·ªçn l√†: <br />
                        <Box component="span" sx={{ fontWeight: 'bold', color: 'primary.main' }}>{finalPrize}</Box>
                    </Typography>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog} color="primary" autoFocus>
                        ƒê√≥ng
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default SpinWheel;
