import React from 'react';
import { Box, Typography, AppBar, Toolbar, Container, CssBaseline, IconButton, InputBase, Grid, Card, CardMedia, CardContent, TextField, Link, Avatar } from '@mui/material';
import { 
    AccountCircle as AccountCircleIcon, 
    Search as SearchIcon, 
    LocationOn as LocationOnIcon,
    Image as ImageIcon // Import icon Image cho placeholder
} from '@mui/icons-material';
import { Link as RouterLink } from 'react-router-dom';

// Import logo
import Logo from '../components/Logo'; 

// D·ªØ li·ªáu m·∫´u cho c√°c m√≥n ƒÉn 
const DUMMY_FOOD_ITEMS = [
    { id: 1, name: 'ÊñôÁêÜÂêçA', comments: 'A„Åï„Çì', date: '30 August 2018' },
    { id: 2, name: 'ÊñôÁêÜÂêçB', comments: 'B„Åï„Çì', date: '30 August 2018' },
    { id: 3, name: 'ÊñôÁêÜÂêçC', comments: 'C„Åï„Çì', date: '30 August 2018' },
];

const Dashboard = () => {
    return (
        <CssBaseline>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    minHeight: '100vh',
                    backgroundColor: '#f5f5f5',
                }}
            >
                {/* === Header (V·ªã tr√≠ 1, 2, 3, 4, 5, 6) === */}
                {/* Gi·ªØ nguy√™n Header */}
                <AppBar position="static" color="default" elevation={1}>
                    <Toolbar sx={{ justifyContent: 'space-between' }}>
                        
                        {/* 1. LOGO (Tr√°i) */}
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                            <Logo size={40} />
                            
                            {/* 2, 3, 4, 5. C√°c n√∫t ƒëi·ªÅu h∆∞·ªõng */}
                            <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 3 }}>
                                <Link component={RouterLink} to="/" color="inherit" underline="none" sx={{ fontWeight: 'bold' }}>„Éõ„Éº„É†</Link>
                                <Link component={RouterLink} to="/about" color="inherit" underline="none">Á¥π‰ªã</Link>
                                <Link component={RouterLink} to="/map" color="inherit" underline="none">Âú∞Âõ≥</Link>
                                <Link component={RouterLink} to="/contact" color="inherit" underline="none">ÈÄ£Áµ°</Link>
                            </Box>
                        </Box>

                        {/* 6. Icon Profile (Ph·∫£i) */}
                        <IconButton 
                            color="inherit" 
                            component={RouterLink} 
                            to="/profile" 
                        >
                            <AccountCircleIcon sx={{ fontSize: 30 }} />
                        </IconButton>
                    </Toolbar>
                </AppBar>

                {/* === Search Bar & Main Content (V·ªã tr√≠ 7, 8, 9) === */}
                <Container 
                    component="main" 
                    maxWidth="lg" 
                     sx={{ 
                         py: 4, 
                        flexGrow: 1,
        // Lo·∫°i b·ªè padding ngang m·∫∑c ƒë·ªãnh c·ªßa Container tr√™n m√†n h√¨nh l·ªõn
        px: { xs: 2, md: 0 } 
    }}
>

                    {/* Thanh T√¨m ki·∫øm (V·ªã tr√≠ 7, 8) - Gi·ªØ nguy√™n */}
                    <Box sx={{ display: 'flex', mb: 4, gap: 2, alignItems: 'center' }}>
                        
                        {/* 7. Input Th√†nh ph·ªë/Khu v·ª±c */}
                        <TextField
                            size="small"
                            label="ÈÉΩÂ∏ÇÂêç„ÇÑÂú∞Âüü„ÇíÂÖ•Âäõ (T√™n th√†nh ph·ªë/khu v·ª±c)"
                            defaultValue="Ha Noi"
                            InputProps={{
                                startAdornment: <LocationOnIcon sx={{ color: 'action.active', mr: 1 }} />,
                            }}
                            sx={{ flexShrink: 0, width: 200 }}
                        />

                        {/* 8. Input T√¨m ki·∫øm M√≥n ƒÉn/Nh√† h√†ng */}
                        <Box sx={{ 
                            flexGrow: 1, 
                            display: 'flex', 
                            border: '1px solid #ccc', 
                            borderRadius: 1, 
                            p: 0.5, 
                            backgroundColor: 'white' 
                        }}>
                            <InputBase
                                placeholder="ÊñôÁêÜ„ÇÑÂ∫óËàó„ÇíÊ§úÁ¥¢„Åô„Çã (T√¨m ki·∫øm m√≥n ƒÉn/c·ª≠a h√†ng)"
                                sx={{ ml: 1, flex: 1 }}
                            />
                            <IconButton type="submit" sx={{ p: '10px' }} aria-label="search">
                                <SearchIcon />
                            </IconButton>
                        </Box>
                    </Box>

                    {/* V·ªã tr√≠ 9: Khu v·ª±c hi·ªÉn th·ªã H√¨nh ·∫£nh l·ªõn/Banner */}
                    <Box 
                        sx={{ 
                            height: 150, // üëà ƒê√£ gi·∫£m chi·ªÅu cao Banner
                            backgroundColor: '#ccc', 
                            mb: 4, 
                            display: 'flex', 
                            alignItems: 'center', 
                            justifyContent: 'center',
                            borderRadius: 1 // Bo g√≥c nh·∫π
                        }}
                    >
                        <Typography variant="h5" color="text.secondary">ÁîªÂÉè (H√¨nh ·∫£nh/Banner)</Typography>
                    </Box>

                    {/* Danh s√°ch c√°c m√≥n ƒÉn (V·ªã tr√≠ 10, 11, 12, 13) */}
                    {/* Grid container spacing={4} v√† md={4} ƒë√£ ƒë·∫£m b·∫£o 3 card chia ƒë·ªÅu tr√™n m√†n h√¨nh l·ªõn */}
                    <Grid container spacing={4}>
                        {DUMMY_FOOD_ITEMS.map((item) => (
                            <Grid item key={item.id} xs={12} sm={6} md={4}> 
                                {/* üëà md={4} ƒë·∫£m b·∫£o 3 card chia ƒë·ªÅu (4+4+4=12) */}
                                <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', boxShadow: 3 }}>
                                    
                                    {/* 10. ·∫¢nh m√≥n ƒÉn (Placeholder theo Form m·∫´u) */}
                                    <Box 
                                        sx={{ 
                                            // V·ªã tr√≠ Placeholder ·∫£nh
                                            pt: '56.25%', // Gi·ªØ t·ª∑ l·ªá 16:9 cho ·∫£nh
                                            backgroundColor: '#f0f0f0', 
                                            display: 'flex', 
                                            alignItems: 'center', 
                                            justifyContent: 'center',
                                            position: 'relative',
                                        }}
                                    >
                                        <ImageIcon sx={{ position: 'absolute', fontSize: 60, color: '#bdbdbd' }} />
                                    </Box>

                                    <CardContent sx={{ 
                                        flexGrow: 1, 
                                        // Padding d·ªçc nh·ªè l·∫°i ƒë·ªÉ d·ªìn n·ªôi dung
                                        py: 1, 
                                        pb: '0 !important',
                                    }}>
                                        {/* 11. T√™n m√≥n ƒÉn (L√†m to l√™n m·ªôt ch√∫t) */}
                                        <Typography gutterBottom variant="h6" component="h2" sx={{ fontWeight: 'bold' }}>
                                            {item.name}
                                        </Typography>
                                        
                                        {/* 12. Link xem chi ti·∫øt */}
                                        <Link component={RouterLink} to={`/details/${item.id}`} variant="body2" sx={{ 
                                            color: 'primary.main', 
                                            textDecoration: 'none', 
                                            display: 'block', 
                                            mb: 1 // Kho·∫£ng c√°ch b√™n d∆∞·ªõi link
                                        }}>
                                            „ÇÇ„Å£„Å®Ë¶ã„Çã (Xem chi ti·∫øt)
                                        </Link>
                                    </CardContent>
                                    
                                    {/* 13. Khu v·ª±c comment/like/date */}
                                    <Box sx={{ 
                                        mt: 2, 
                                        borderTop: '1px solid #eee', 
                                        pt: 1, 
                                        p: 2, // Th√™m padding ƒë·ªÉ card tr√¥ng l·ªõn h∆°n
                                    }}>
                                        <Typography variant="body2" color="text.secondary">„Ç≥„É°„É≥„Éà (Comment)</Typography>
                                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mt: 0.5 }}>
