// src/context/AuthContext.jsx

import React, { createContext, useState, useContext } from 'react';

// 1. Khởi tạo Context. Giá trị mặc định là null.
const AuthContext = createContext(null);

// 2. Tạo Provider Component để bao bọc ứng dụng
export const AuthProvider = ({ children }) => {
    // Khởi tạo state: 
    // Mặc định là false, hoặc kiểm tra token trong localStorage.
    const [auth, setAuth] = useState({ 
        isLoggedIn: false, 
        user: null 
    }); 

    // Hàm login/logout (Tùy chọn, để tiện gọi)
    const login = (userData) => {
        // Lưu token vào localStorage (thực tế)
        // localStorage.setItem('token', userData.token); 
        setAuth({ isLoggedIn: true, user: userData.user });
    };

    const logout = () => {
        // Xóa token khỏi localStorage (thực tế)
        // localStorage.removeItem('token'); 
        setAuth({ isLoggedIn: false, user: null });
    };


    // Giá trị sẽ được cung cấp cho tất cả các component con
    const value = { auth, setAuth, login, logout };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};

// 3. Custom Hook để sử dụng Context dễ dàng hơn
export const useAuth = () => {
    return useContext(AuthContext);
};
