    document.getElementById('dashboard-search-form')?.addEventListener('submit', (e) => {
        e.preventDefault();
        const city = document.getElementById('city-input').value;
        const query = document.getElementById('search-query-input').value;
        const loading = document.getElementById('loading-indicator');

        loading.classList.remove('hidden');
        loading.classList.add('flex');

        console.log(`Searching for: ${city} and ${query}`);

        setTimeout(() => {
            loading.classList.remove('flex');
            loading.classList.add('hidden');
            // Tại đây, bạn sẽ thực hiện fetch API thực tế đến Backend Node.js
        }, 1000);
    });

    // Render lại icons sau khi nội dung được thêm vào DOM
    lucide.createIcons();
}

