import React, { useState } from 'react';
import {
  TextField,
  Button,
  Box,
  Typography,
  Checkbox,
  FormControlLabel,
  Link, // Dùng để tạo link nếu cần
  Stack, // Thư viện MUI để sắp xếp các phần tử dễ dàng
} from '@mui/material';
import { Link as RouterLink, useNavigate } from 'react-router-dom'; // Dùng RouterLink để chuyển hướng
import { register } from '../api/api'; // SỬA: Import đúng tên hàm là 'register'

const Register = () => {
  // Đổi tên state 'name' thành 'username' để rõ ràng hơn (theo form gốc là ユーザー名)
  const [username, setUsername] = useState(''); // 2. ユーザー名
  const [email, setEmail] = useState(''); // 3. メール
  const [password, setPassword] = useState(''); // 4. パスワード
  const [confirmPassword, setConfirmPassword] = useState(''); // 5. パスワードをもう一度入力してください
  const [agreedToTerms, setAgreedToTerms] = useState(false); // 6. ユーザー権利に同意する (Đổi tên state cho rõ ràng)
  const [errors, setErrors] = useState({}); // State để lưu trữ các lỗi
  const navigate = useNavigate();

  // Hàm xác thực dữ liệu
  const validate = () => {
    const newErrors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!emailRegex.test(email)) {
      newErrors.email = 'メールの形式が正しくありません (Định dạng email không hợp lệ)';
    }

    if (password.length < 6) {
      newErrors.password = 'パスワードは6文字以上である必要があります (Mật khẩu phải có ít nhất 6 ký tự)';
    }

    if (password !== confirmPassword) {
      newErrors.confirmPassword = 'パスワードが一致しません (Mật khẩu không khớp)';
    }

    if (!agreedToTerms) {
      newErrors.terms = 'ユーザー権利に同意してください (Vui lòng đồng ý với điều khoản)';
    }

    setErrors(newErrors);
    // Trả về true nếu không có lỗi
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Nếu xác thực thất bại, dừng lại
    if (!validate()) {
      return;
    }

    try {
      await register({ username, email, password });
      alert('登録が成功しました。ログインページに移動します。(Đăng ký thành công. Chuyển đến trang đăng nhập.)');
      navigate('/login');
    } catch (error) {
      // Xử lý lỗi từ server
      if (error.response && error.response.status === 400) {
        // Giả sử backend trả về lỗi "Email already exists"
        setErrors((prevErrors) => ({
          ...prevErrors,
          email: 'このメールアドレスは既に使用されています (Email này đã được sử dụng)',
        }));
      } else {
        console.error('Registration error:', error);
        alert('登録に失敗しました (Đăng ký thất bại).');
      }
    }
  };

  return (
    <Box
      sx={{
        maxWidth: 400,
        margin: "auto",
        mt: 10,
        padding: 3,
        boxShadow: 3,
        borderRadius: 2,
        backgroundColor: 'white',
      }}
    >
      {/* 1. LOGO Placeholder (Theo thiết kế gốc) */}
      <Box
        sx={{
          border: "1px solid #ccc",
          width: 80,
          height: 30,
          margin: "0 auto 30px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Typography variant="body2">LOGO</Typography>
      </Box>

      {/* Tiêu đề ẩn, vì thiết kế gốc chỉ có Logo */}
      {/* <Typography variant="h5" align="center" mb={3}>アカウント作成</Typography> */}

      <form onSubmit={handleSubmit}>
        {/* 2. ユーザー名 (Username) */}
        <TextField
          label="ユーザー名"
          variant="outlined"
          fullWidth
          margin="normal"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          error={!!errors.username}
          helperText={errors.username}
        />
        {/* 3. メール (Email) */}
        <TextField
          label="メール"
          variant="outlined"
          fullWidth
          margin="normal"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          error={!!errors.email}
          helperText={errors.email}
        />
        {/* 4. パスワード (Password) */}
        <TextField
          label="パスワード"
          type="password"
          variant="outlined"
          fullWidth
          margin="normal"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          error={!!errors.password}
          helperText={errors.password}
        />
        {/* 5. パスワードをもう一度入力してください (Confirm Password) */}
        <TextField
          label="パスワードをもう一度入力してください"
          type="password"
          variant="outlined"
          fullWidth
          margin="normal"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
          error={!!errors.confirmPassword}
          helperText={errors.confirmPassword}
        />

        {/* 6. Checkbox Điều khoản (ユーザー権利に同意する) */}
        <FormControlLabel
          control={
            <Checkbox
              checked={agreedToTerms}
              onChange={(e) => setAgreedToTerms(e.target.checked)}
              name="agreeTerms"
              color="primary"
            />
          }
          label={
            // Dùng Link MUI để tạo liên kết cho "ユーザー権利"
            <Typography variant="body2">
              <Link href="#" color="primary" underline="hover">
                ユーザー権利
              </Link>
              に同意する
            </Typography>
          }
          sx={{ mt: 1, mb: 1, justifyContent: 'flex-start', width: '100%' }}
        />
        {/* Hiển thị lỗi cho checkbox */}
        {errors.terms && <Typography color="error" variant="caption" sx={{ display: 'block', ml: 2, mb: 2 }}>{errors.terms}</Typography>}

        {/* 7 & 8. Các nút hành động (Dùng Stack để bố cục ngang) */}
        <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
          {/* 7. Nút Đăng ký (アカウントを作成する) - Primary */}
          <Button
            type="submit"
            variant="contained"
            color="primary"
            disabled={!agreedToTerms} // Vô hiệu hóa nếu chưa đồng ý điều khoản
            sx={{ flexGrow: 1 }} // Chiếm hết phần không gian còn lại
          >
            アカウントを作成する
          </Button>

          {/* 8. Nút Đăng nhập (ログイン) - Secondary */}
          <Button
            component={RouterLink} // Sử dụng RouterLink để chuyển hướng
            to="/login" // Thay đổi đường dẫn thực tế của bạn
            variant="outlined"
            color="secondary"
            sx={{ minWidth: 100 }} // Đặt kích thước nhỏ hơn nút chính
          >
            ログイン
          </Button>
        </Stack>
      </form>
    </Box>
  );
};

export default Register;
