import logo from './logo.svg';
import './App.css';
import { createIcons, allIcons } from 'lucide';
import { Zap } from 'lucide-react'; // ✅ Correct package name

function App() {
    return (
        <div class="min-h-screen bg-gray-100 flex flex-col p-4">
            <header class="w-full bg-white shadow-md rounded-lg p-4 flex justify-between items-center max-w-7xl mx-auto mt-4">
                <div class="font-bold text-xl text-indigo-600">LOGO</div>

                <nav class="flex space-x-4">
                    <button id="nav-login-btn" class="flex items-center px-4 py-2 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 hover:bg-gray-50 transition duration-150 shadow-sm">
                        <i data-lucide="log-in" class="w-4 h-4 mr-2"></i>Đăng nhập
                    </button>
                    <button id="nav-register-btn" class="flex items-center px-4 py-2 border border-transparent rounded-lg text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 transition duration-150 shadow-md">
                        <i data-lucide="user-plus" class="w-4 h-4 mr-2"></i>Đăng ký
                    </button>
                </nav>
            </header>

            <main class="flex-grow flex flex-col items-center justify-center text-center p-8">
                <h1 class="text-6xl font-extrabold text-gray-900 mb-6">TÊN CHƯƠNG TRÌNH</h1>
                
                <p class="text-xl text-gray-600 max-w-3xl">Chào mừng bạn đến với Ứng dụng độc đáo của chúng tôi. Chúng tôi cung cấp các giải pháp tiên tiến để tối ưu hóa công việc và cuộc sống của bạn. Hãy đăng ký ngay để bắt đầu trải nghiệm!</p>
                <div class="mt-10">
                    <button id="explore-btn" class="px-8 py-4 text-lg font-semibold text-white bg-green-500 rounded-lg shadow-xl hover:bg-green-600 transition duration-200 transform hover:scale-105">
                        Khám Phá Ngay
                    </button>
                </div>
            </main>

            <footer class="w-full bg-white text-center p-4 text-sm text-gray-500 shadow-inner rounded-lg max-w-7xl mx-auto mb-4">
                &copy; 2024 Tên Chương Trình. Bảo lưu mọi quyền.
            </footer>
        </div>
    );
    
    // Gán sự kiện chuyển trang
    // document.getElementById('nav-login-btn').addEventListener('click', () => navigateTo('login'));
    // document.getElementById('nav-register-btn').addEventListener('click', () => navigateTo('register'));
    // document.getElementById('explore-btn').addEventListener('click', () => navigateTo('register'));

    // Khởi tạo icon
    // createIcons({ icons: allIcons });
}
export default App;
