import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => {
    return useContext(AuthContext);
};

export const AuthWrapper = ({ children }) => {
    const [user, setUser] = useState(null); // User object hoặc null
    const [token, setToken] = useState(localStorage.getItem('token')); // Lấy token từ localStorage
    const [isCheckingAuth, setIsCheckingAuth] = useState(true); // Trạng thái đang kiểm tra

    const navigate = useNavigate();

    useEffect(() => {
        // 1. Kiểm tra Token khi khởi động
        if (token) {
            // Logic giải mã token hoặc gửi yêu cầu /me để lấy thông tin user
            // Nếu token hợp lệ:
            // setUser(decodedUser);
            // setIsCheckingAuth(false);
        } else {
            // Không có token, không đăng nhập
            setUser(null);
        }
        setIsCheckingAuth(false);
    }, [token]);

    const login = (userData, jwtToken) => {
        setUser(userData);
        setToken(jwtToken);
        localStorage.setItem('token', jwtToken); // Lưu token
        navigate('/dashboard'); // Chuyển hướng đến Dashboard sau khi đăng nhập
    };

    const logout = () => {
        setToken(null);
        setUser(null);
        localStorage.removeItem('token');
        navigate('/login'); // Chuyển hướng đến trang Login sau khi đăng xuất
    };

    // ⚠️ Nếu bạn đã cố định ở đây, HÃY XÓA NÓ:
    // const [isLoggedIn, setIsLoggedIn] = useState(true); // XÓA HOẶC ĐẶT LẠI

    return (
        <AuthContext.Provider value={{ user, token, login, logout, isCheckingAuth, isLoggedIn: !!user }}>
            {/* Hiển thị loading nếu đang kiểm tra Auth */}
            {isCheckingAuth ? <div>Loading Authentication...</div> : children}
        </AuthContext.Provider>
    );
};
