import React, { useState } from 'react';
import { Box, Button, Typography, Dialog, DialogTitle, DialogContent, DialogActions, Paper } from '@mui/material';
import Roulette from 'react-roulette-pro'; 
import FastfoodIcon from '@mui/icons-material/Fastfood'; 
import 'react-roulette-pro/dist/index.css'; // Quan trọng: Đảm bảo CSS được import

// Danh sách các món ăn để quay
const FOOD_OPTIONS = [
    'Phở Bò', 'Bún Chả', 'Nem Rán', 'Bánh Mì', 'Gỏi Cuốn', 
    'Cơm Tấm', 'Mì Quảng', 'Hủ Tiếu', 'Bún Bò Huế', 'Cháo Lòng'
];

// Chuyển đổi dữ liệu cho react-roulette-pro
const prizes = FOOD_OPTIONS.map((food, index) => ({
    id: index, // Thêm id cho mỗi prize
    text: food,
    // Màu sắc được thiết lập trong designOptions, nhưng có thể ghi đè ở đây
    // backgroundColor: index % 2 === 0 ? '#ffb74d' : '#ff8a65', 
}));

const SpinWheel = ({ onFoodSelected }) => {
    const [openResultDialog, setOpenResultDialog] = useState(false);
    const [openWheelModal, setOpenWheelModal] = useState(false); // Dùng Modal thay cho Dialog để chứa wheel
    const [mustSpin, setMustSpin] = useState(false); 
    const [prizeNumber, setPrizeNumber] = useState(0); 
    const [finalPrize, setFinalPrize] = useState(''); 

    // Xử lý khi vòng quay dừng lại và món ăn được chọn
    const handleSpinStop = (prize) => {
        const selectedFood = prizes[prizeNumber].text;
        
        setFinalPrize(selectedFood);
        setMustSpin(false); 
        
        // Đóng Modal chứa vòng quay ngay lập tức
        setOpenWheelModal(false); 
        
        // Mở Dialog kết quả sau một khoảng trễ nhỏ
        setTimeout(() => setOpenResultDialog(true), 300);

        if (onFoodSelected) {
            onFoodSelected(selectedFood);
        }
    };

    // Bắt đầu quay
    const handleSpin = () => {
        // 1. Chọn index ngẫu nhiên (từ 0 đến length - 1)
        const randomIndex = Math.floor(Math.random() * prizes.length);
        setPrizeNumber(randomIndex);
        
        // 2. Mở Modal chứa vòng quay
        setOpenWheelModal(true); 

        // 3. Kích hoạt quay
        // Dùng setTimeout để đảm bảo Modal đã mở trước khi bắt đầu quay (tránh lỗi render)
        setTimeout(() => setMustSpin(true), 100); 
    };

    const handleCloseResultDialog = () => {
        setOpenResultDialog(false);
        setFinalPrize('');
    };

    return (
        <Box sx={{ 
            position: 'fixed', 
            bottom: 30, 
            right: 30, 
            zIndex: 1000, 
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
        }}>
            {/* Nút kích hoạt */}
            <Button
                variant="contained"
                color="primary"
                size="large"
                sx={{
                    borderRadius: '50%', 
                    width: 80,
                    height: 80,
                    minWidth: 0, 
                    boxShadow: 6,
                    mb: 1, 
                    '&:hover': {
                        transform: 'scale(1.05)',
                        transition: 'transform 0.2s ease-in-out',
                    }
                }}
                onClick={handleSpin}
                aria-label="Quay để chọn món ăn"
            >
                <FastfoodIcon sx={{ fontSize: 40 }} />
            </Button>
            <Typography variant="caption" sx={{ color: 'text.secondary', fontWeight: 'bold' }}>Quay Món!</Typography>
            
            {/* Modal chứa vòng quay */}
            <Dialog
                open={openWheelModal}
                // Ngăn chặn đóng khi click ra ngoài hoặc nhấn ESC trong khi quay
                onClose={() => setOpenWheelModal(false)} 
                sx={{ '& .MuiDialog-paper': { maxWidth: 'none', background: 'transparent', boxShadow: 'none' } }}
            >
                <Roulette
                    prizes={prizes}
                    start={mustSpin}
                    winningPrize={prizeNumber}
                    onPrizeSelected={handleSpinStop} // Khi vòng quay dừng lại
                    // Tùy chỉnh kích thước và thiết kế
                    designOptions={{
                        // Đảm bảo kích thước phù hợp
                        prizeItemWidth: 120, 
                        prizeItemHeight: 120, 
                        prizesWithText: true,
                        // Thiết lập màu sắc xen kẽ
                        prizeItemColors: [
                            '#FF6347', '#FFD700', '#ADFF2F', '#87CEEB', '#BA55D3', 
                            '#FF4500', '#DAA520', '#3CB371', '#4169E1', '#9370DB'
                        ],
                        // Tùy chỉnh mũi tên chỉ báo
                        pointerColor: '#533c02',
                        pointerBackgroundColor: '#f7d057',
                        // Đặt kích thước vòng quay
                        rouletteSize: 400, 
                        
                    }}
                    options={{
                        stopInCenter: true,
                    }}
                />
                
            </Dialog>


            {/* Dialog hiển thị kết quả */}
            <Dialog open={openResultDialog} onClose={handleCloseResultDialog}>
                <DialogTitle sx={{ textAlign: 'center' }}>Chúc mừng!</DialogTitle>
                <DialogContent>
                    <Typography variant="h5" align="center" sx={{ my: 2 }}>
                        Món ăn được chọn là: <br />
                        <Box component="span" sx={{ fontWeight: 'bold', color: 'primary.main' }}>{finalPrize}</Box>
                    </Typography>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseResultDialog} color="primary" autoFocus>
                        Đóng
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default SpinWheel;
