// frontend/src/context/AuthContext.js

import React, { createContext, useState, useEffect } from 'react';

// 1. Khởi tạo Context
export const AuthContext = createContext();

// 2. Tạo Provider Component
export const AuthProvider = ({ children }) => {
  const [auth, setAuth] = useState({ 
    isAuthenticated: false,
    user: null,
    // Bạn có thể đọc token/user từ localStorage khi khởi tạo
  });

  // useEffect để kiểm tra token/user khi ứng dụng khởi động
  useEffect(() => {
    // Logic kiểm tra token trong localStorage và thiết lập auth state
    const token = localStorage.getItem('token');
    if (token) {
      // Giả sử có hàm giải mã token để lấy thông tin user
      // Ví dụ: const user = decodeToken(token);
      setAuth({ 
        isAuthenticated: true, 
        user: { email: "example@user.com", username: "user" } // Thay bằng logic thực tế
      });
    }
  }, []);

  return (
    // Giá trị cung cấp cho các component con
    <AuthContext.Provider value={{ auth, setAuth }}>
      {children}
    </AuthContext.Provider>
  );
};

// Lưu ý: Bạn cần bao bọc component <App /> bằng <AuthProvider> trong index.js hoặc App.js 
// để AuthContext hoạt động.
