  Button,
  Box,
  Typography,
  Checkbox,
  FormControlLabel,
  Link,
  Stack,
} from '@mui/material';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import { register } from '../api/api'; 
import Logo from '../components/Logo';
const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [agreedToTerms, setAgreedToTerms] = useState(false);
  const [errors, setErrors] = useState({}); // State ƒë·ªÉ l∆∞u tr·ªØ c√°c l·ªói
  const navigate = useNavigate();

  // H√†m x√°c th·ª±c d·ªØ li·ªáu cho t·∫•t c·∫£ c√°c tr∆∞·ªùng
  const validate = (field, value) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    let errorMsg = '';

    switch (field) {
      case 'username':
        if (!value.trim()) errorMsg = '„É¶„Éº„Ç∂„ÉºÂêç„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ (Vui l√≤ng nh·∫≠p t√™n ng∆∞·ªùi d√πng)';
        break;
      case 'email':
        if (!emailRegex.test(value)) errorMsg = '„É°„Éº„É´„ÅÆÂΩ¢Âºè„ÅåÊ≠£„Åó„Åè„ÅÇ„Çä„Åæ„Åõ„Çì (ƒê·ªãnh d·∫°ng email kh√¥ng h·ª£p l·ªá)';
        break;
      case 'password':
        if (value.length < 6) errorMsg = '„Éë„Çπ„ÉØ„Éº„Éâ„ÅØ6ÊñáÂ≠ó‰ª•‰∏ä„Åß„ÅÇ„ÇãÂøÖË¶Å„Åå„ÅÇ„Çä„Åæ„Åô (M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±)';
        break;
      case 'confirmPassword':
        if (value !== password) errorMsg = '„Éë„Çπ„ÉØ„Éº„Éâ„Åå‰∏ÄËá¥„Åó„Åæ„Åõ„Çì (M·∫≠t kh·∫©u kh√¥ng kh·ªõp)';
        break;
      default:
        break;
    }
    return errorMsg;
  };

  // H√†m x·ª≠ l√Ω s·ª± ki·ªán khi ng∆∞·ªùi d√πng thay ƒë·ªïi gi√° tr·ªã input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    
    // 1. C·∫≠p nh·∫≠t State
    if (name === 'username') setUsername(value);
    if (name === 'email') setEmail(value);
    if (name === 'password') setPassword(value);
    if (name === 'confirmPassword') setConfirmPassword(value);

    // 2. X√≥a l·ªói c≈© ƒë·ªÉ hi·ªÉn th·ªã l·ªói m·ªõi sau onBlur
    setErrors((prev) => ({ ...prev, [name]: '' }));
  };

  // H√†m x·ª≠ l√Ω khi ng∆∞·ªùi d√πng r·ªùi kh·ªèi tr∆∞·ªùng input (onBlur)
  const handleBlur = (e) => {
    const { name, value } = e.target;
    // Ch·∫°y x√°c th·ª±c cho tr∆∞·ªùng v·ª´a r·ªùi kh·ªèi
    const errorMsg = validate(name, value);

    if (errorMsg) {
      setErrors((prev) => ({ ...prev, [name]: errorMsg }));
    } else {
      setErrors((prev) => ({ ...prev, [name]: '' }));
    }

    // ƒê·∫∑c bi·ªát ki·ªÉm tra l·∫°i confirmPassword khi password b·ªã blur
    if (name === 'password' && confirmPassword.length > 0) {
        const confirmError = validate('confirmPassword', confirmPassword);
        setErrors((prev) => ({ ...prev, confirmPassword: confirmError }));
    }
  };

  // H√†m x√°c th·ª±c cu·ªëi c√πng tr∆∞·ªõc khi submit
  const validateAll = () => {
    const newErrors = {};
    const inputs = { username, email, password, confirmPassword };
    
    // Ki·ªÉm tra t·∫•t c·∫£ c√°c tr∆∞·ªùng
    Object.keys(inputs).forEach(field => {
        const errorMsg = validate(field, inputs[field]);
        if (errorMsg) {
            newErrors[field] = errorMsg;
        }
    });

    if (!agreedToTerms) {
      newErrors.terms = '„É¶„Éº„Ç∂„ÉºÊ®©Âà©„Å´ÂêåÊÑè„Åó„Å¶„Åè„Å†„Åï„ÅÑ (Vui l√≤ng ƒë·ªìng √Ω v·ªõi ƒëi·ªÅu kho·∫£n)';
    }

