      navigate('/login');
    } catch (error) {
      // ... (X·ª≠ l√Ω l·ªói API gi·ªØ nguy√™n)
      if (error.response && error.response.status === 400) {
        setErrors((prevErrors) => ({
          ...prevErrors,
          email: '„Åì„ÅÆ„É°„Éº„É´„Ç¢„Éâ„É¨„Çπ„ÅØÊó¢„Å´‰ΩøÁî®„Åï„Çå„Å¶„ÅÑ„Åæ„Åô (Email n√†y ƒë√£ ƒë∆∞·ª£c s·ª≠ d·ª•ng)',
