        {/* 2. „É¶„Éº„Ç∂„ÉºÂêç (Username) */}
        <TextField
          label="„É¶„Éº„Ç∂„ÉºÂêç"
          variant="outlined"
          fullWidth
          margin="normal"
          name="username" // Th√™m name
          value={username}
          onChange={handleInputChange} // D√πng handleInputChange
          onBlur={handleBlur} // üõë K√≠ch ho·∫°t x√°c th·ª±c onBlur
          required
          error={!!errors.username} // Khung ƒë·ªè
          helperText={errors.username} // D√≤ng c·∫£nh b√°o
        />
        {/* 3. „É°„Éº„É´ (Email) */}
        <TextField
          label="„É°„Éº„É´"
          variant="outlined"
          fullWidth
          margin="normal"
          type="email"
          name="email" // Th√™m name
          value={email}
          onChange={handleInputChange}
          onBlur={handleBlur} // üõë K√≠ch ho·∫°t x√°c th·ª±c onBlur
          required
          error={!!errors.email}
          helperText={errors.email}
