          helperText={errors.password}
        />
        {/* 5. „Éë„Çπ„ÉØ„Éº„Éâ„Çí„ÇÇ„ÅÜ‰∏ÄÂ∫¶ÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ (Confirm Password) */}
        <TextField
          label="„Éë„Çπ„ÉØ„Éº„Éâ„Çí„ÇÇ„ÅÜ‰∏ÄÂ∫¶ÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ"
          type="password"
          variant="outlined"
          fullWidth
          margin="normal"
          name="confirmPassword" // Th√™m name
          value={confirmPassword}
          onChange={handleInputChange}
          onBlur={handleBlur} // üõë K√≠ch ho·∫°t x√°c th·ª±c onBlur
          required
          error={!!errors.confirmPassword}
          helperText={errors.confirmPassword}
        />

        {/* 6. Checkbox ƒêi·ªÅu kho·∫£n („É¶„Éº„Ç∂„ÉºÊ®©Âà©„Å´ÂêåÊÑè„Åô„Çã) */}
        <FormControlLabel
          control={
            <Checkbox
              checked={agreedToTerms}
              // Khi thay ƒë·ªïi checkbox, x√≥a l·ªói terms (n·∫øu c√≥)
              onChange={(e) => {
                  setAgreedToTerms(e.target.checked);
                  setErrors(prev => ({ ...prev, terms: '' }));
              }}
              name="agreeTerms"
              color="primary"
            />
          }
          label={
            <Typography variant="body2">
              <Link href="#" color="primary" underline="hover">
                „É¶„Éº„Ç∂„ÉºÊ®©Âà©
              </Link>
              „Å´ÂêåÊÑè„Åô„Çã
            </Typography>
          }
          sx={{ mt: 1, mb: 1, justifyContent: 'flex-start', width: '100%' }}
        />
        {/* Hi·ªÉn th·ªã l·ªói cho checkbox */}
        {errors.terms && <Typography color="error" variant="caption" sx={{ display: 'block', ml: 2, mb: 2 }}>{errors.terms}</Typography>}

        {/* 7 & 8. C√°c n√∫t h√†nh ƒë·ªông */}
        <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
          {/* 7. N√∫t ƒêƒÉng k√Ω („Ç¢„Ç´„Ç¶„É≥„Éà„Çí‰ΩúÊàê„Åô„Çã) - Primary */}
          <Button
            type="submit"
            variant="contained"
            color="primary"
            disabled={!agreedToTerms}
            sx={{ flexGrow: 1 }}
          >
            „Ç¢„Ç´„Ç¶„É≥„Éà„Çí‰ΩúÊàê„Åô„Çã
          </Button>

          {/* 8. N√∫t ƒêƒÉng nh·∫≠p („É≠„Ç∞„Ç§„É≥) - Secondary */}
          <Button
            component={RouterLink}
            to="/login"
            variant="outlined"
            color="secondary"
            sx={{ minWidth: 100 }}
          >
            „É≠„Ç∞„Ç§„É≥
          </Button>
        </Stack>
      </form>
    </Box>
  );
};

export default Register;
