import React, { useState } from 'react';
import { Box, Button, Typography, Dialog, DialogTitle, DialogContent, DialogActions, Modal } from '@mui/material';
import Roulette from 'react-roulette-pro'; 
import FastfoodIcon from '@mui/icons-material/Fastfood'; 
import 'react-roulette-pro/dist/index.css'; 

// --- COMPONENT SPIN WHEEL ---
const SpinWheel = ({ foodItems = [], onFoodSelected }) => {
    const [openResultDialog, setOpenResultDialog] = useState(false);
    const [openWheelModal, setOpenWheelModal] = useState(false); 
    const [mustSpin, setMustSpin] = useState(false); 
    const [prizeNumber, setPrizeNumber] = useState(0); 
    const [finalPrize, setFinalPrize] = useState(''); 

    // Chuyển đổi dữ liệu từ props để react-roulette-pro có thể sử dụng
    const prizes = foodItems.map((item, index) => ({
        id: item.id || index, // Sử dụng id từ item nếu có
        text: item.name,
    }));

    const handleSpinStop = (prize) => {
        const selectedFood = prizes[prizeNumber].text;
        
        setFinalPrize(selectedFood);
        setMustSpin(false); 
        setOpenWheelModal(false); 
        
        setTimeout(() => setOpenResultDialog(true), 300);

        if (onFoodSelected) {
            onFoodSelected(selectedFood);
        }
    };

    const handleSpin = () => {
        // Không mở vòng quay nếu không có món ăn nào
        if (prizes.length === 0) {
            alert('Vui lòng chọn một địa điểm có món ăn.');
            return;
        }

        const randomIndex = Math.floor(Math.random() * prizes.length);
        setPrizeNumber(randomIndex);
        
        setOpenWheelModal(true); 
        setTimeout(() => setMustSpin(true), 100); 
    };

    const handleCloseResultDialog = () => {
        setOpenResultDialog(false);
        setFinalPrize('');
    };

    return (
        <Box sx={{ 
            position: 'fixed', 
            bottom: 30, 
            right: 30, 
            zIndex: 1000, 
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
        }}>
            {/* Nút nhỏ ở góc dưới bên phải */}
            <Button
                variant="contained"
                color="primary"
                size="large"
                sx={{
                    borderRadius: '50%', 
                    width: 80,
                    height: 80,
                    minWidth: 0, 
                    boxShadow: 6,
                    mb: 1, 
                    '&:hover': {
                        transform: 'scale(1.05)',
                        transition: 'transform 0.2s ease-in-out',
                    }
                }}
                onClick={handleSpin}
                aria-label="Quay để chọn món ăn"
            >
                <FastfoodIcon sx={{ fontSize: 40 }} />
            </Button>
            <Typography variant="caption" sx={{ color: 'text.secondary', fontWeight: 'bold' }}>Quay Món!</Typography>
            
            {/* Modal chứa vòng quay */}
            <Modal
                open={openWheelModal}
                onClose={() => !mustSpin && setOpenWheelModal(false)} // Chỉ cho phép đóng khi không quay
                aria-labelledby="wheel-modal-title"
                sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}
            >
                {/* Box này chứa vòng quay và các phần tử liên quan */}
                <Box sx={{ outline: 'none', textAlign: 'center' }}>
                     <Roulette
                         prizes={prizes}
                         start={mustSpin}
                         winningPrize={prizeNumber}
                         onPrizeSelected={handleSpinStop} 
                         // Tùy chỉnh kích thước và thiết kế để có hình tròn và text
                         designOptions={{
                             prizeItemWidth: 100, // Chiều rộng mỗi phần
                             prizeItemHeight: 100, // Chiều cao mỗi phần
                             prizesWithText: true, // Hiển thị text
                             prizeItemColors: [
                                 '#FF6347', '#FFD700', '#ADFF2F', '#87CEEB', '#BA55D3', 
                                 '#FF4500', '#DAA520', '#3CB371', '#4169E1', '#9370DB',
                                 '#F08080', '#DDA0DD', '#7B68EE', '#B0E0E6', '#FFDAB9'
                             ],
                             pointerColor: '#533c02',
                             pointerBackgroundColor: '#f7d057',
                             rouletteSize: 500, // Kích thước tổng thể của vòng quay
                             hideSpinButton: true, // Ẩn nút Start bên trong vòng quay
                         }}
                         options={{
                             stopInCenter: true,
                         }}
                     />
                     {/* Nút START riêng biệt bên dưới vòng quay */}
                     <Button
                         variant="contained"
                         color="primary"
                         size="large"
                         sx={{ mt: 3, width: 200, py: 1.5, fontSize: '1.2rem' }}
                         onClick={() => setMustSpin(true)} // Chỉ kích hoạt quay
                         disabled={mustSpin} // Vô hiệu hóa khi đang quay
                     >
                         スタート (START)
                     </Button>
                </Box>
            </Modal>

            {/* Dialog hiển thị kết quả */}
            <Dialog open={openResultDialog} onClose={handleCloseResultDialog}>
                <DialogTitle sx={{ textAlign: 'center' }}>Chúc mừng!</DialogTitle>
                <DialogContent>
                    <Typography variant="h5" align="center" sx={{ my: 2 }}>
                        Món ăn được chọn là: <br />
                        <Box component="span" sx={{ fontWeight: 'bold', color: 'primary.main' }}>{finalPrize}</Box>
                    </Typography>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseResultDialog} color="primary" autoFocus>
                        Đóng
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default SpinWheel;
