import React, { useState } from 'react';
import { Box, Button, Typography, Dialog, DialogTitle, DialogContent, DialogActions, Modal } from '@mui/material';
import Roulette from 'react-roulette-pro'; // üëà S·ª≠a th√†nh default import
import FastfoodIcon from '@mui/icons-material/Fastfood'; // Icon cho v√≤ng quay

// Danh s√°ch c√°c m√≥n ƒÉn ƒë·ªÉ quay
const FOOD_OPTIONS = [
    'Ph·ªü B√≤', 'B√∫n Ch·∫£', 'Nem R√°n', 'B√°nh M√¨', 'G·ªèi Cu·ªën', 
    'C∆°m T·∫•m', 'M√¨ Qu·∫£ng', 'H·ªß Ti·∫øu', 'B√∫n B√≤ Hu·∫ø', 'Ch√°o L√≤ng'
];

// Chuy·ªÉn ƒë·ªïi d·ªØ li·ªáu cho react-roulette-pro
const prizes = FOOD_OPTIONS.map(food => ({
    text: food,
    // B·∫°n c√≥ th·ªÉ th√™m ·∫£nh cho t·ª´ng m√≥n ƒÉn ·ªü ƒë√¢y n·∫øu mu·ªën
    // image: 'path/to/image.png', 
}));

const SpinWheel = ({ onFoodSelected }) => {
    const [openResultDialog, setOpenResultDialog] = useState(false);
    const [openWheelDialog, setOpenWheelDialog] = useState(false); // State cho dialog v√≤ng quay
    const [mustSpin, setMustSpin] = useState(false); // State ƒë·ªÉ k√≠ch ho·∫°t v√≤ng quay
    const [prizeNumber, setPrizeNumber] = useState(0); // Index c·ªßa m√≥n ƒÉn tr√∫ng th∆∞·ªüng
    const [finalPrize, setFinalPrize] = useState(''); // T√™n m√≥n ƒÉn cu·ªëi c√πng

    // X·ª≠ l√Ω khi v√≤ng quay d·ª´ng l·∫°i
    const handleSpinStop = () => {
        setFinalPrize(prizes[prizeNumber].text);
        setMustSpin(false); // D·ª´ng tr·∫°ng th√°i quay
        setOpenWheelDialog(false); // ƒê√≥ng dialog v√≤ng quay
        
        // M·ªü dialog k·∫øt qu·∫£ sau m·ªôt kho·∫£ng tr·ªÖ nh·ªè ƒë·ªÉ hi·ªáu ·ª©ng m∆∞·ª£t h∆°n
        setTimeout(() => setOpenResultDialog(true), 300);

        if (onFoodSelected) {
            onFoodSelected(prizes[prizeNumber].text);
        }
    };

    // B·∫Øt ƒë·∫ßu quay
    const handleSpin = () => {
        const randomIndex = Math.floor(Math.random() * FOOD_OPTIONS.length);
        setPrizeNumber(randomIndex);
        setOpenWheelDialog(true); // M·ªü dialog ch·ª©a v√≤ng quay
        setMustSpin(true); // K√≠ch ho·∫°t v√≤ng quay
    };

    const handleCloseDialog = () => {
        setOpenResultDialog(false);
        setFinalPrize('');
    };

    return (
        <Box sx={{ 
            position: 'fixed', // V·ªã tr√≠ c·ªë ƒë·ªãnh tr√™n m√†n h√¨nh
            bottom: 30,        // C√°ch ƒë√°y 30px
            right: 30,         // C√°ch ph·∫£i 30px
            zIndex: 1000,      // ƒê·∫£m b·∫£o n·∫±m tr√™n c√°c ph·∫ßn t·ª≠ kh√°c
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
        }}>
            {/* N√∫t ƒë·ªÉ k√≠ch ho·∫°t v√≤ng quay (·∫©n/hi·ªán v√≤ng quay) */}
            <Button
                variant="contained"
                color="primary"
                size="large"
                sx={{
                    borderRadius: '50%', // L√†m cho n√∫t tr√≤n
                    width: 80,
                    height: 80,
                    minWidth: 0, // Ghi ƒë√® min-width m·∫∑c ƒë·ªãnh
                    boxShadow: 6,
                    mb: 1, // Kho·∫£ng c√°ch v·ªõi v√≤ng quay (n·∫øu c√≥)
                    // Animation nh·∫π khi hover
                    '&:hover': {
                        transform: 'scale(1.05)',
                        transition: 'transform 0.2s ease-in-out',
                    }
                }}
                onClick={handleSpin}
                aria-label="Quay ƒë·ªÉ ch·ªçn m√≥n ƒÉn"
            >
                <FastfoodIcon sx={{ fontSize: 40 }} />
            </Button>
            <Typography variant="caption" sx={{ color: 'text.secondary', fontWeight: 'bold' }}>Quay M√≥n!</Typography>
            
            {/* Dialog ch·ª©a v√≤ng quay */}
            <Modal
                open={openWheelDialog}
                onClose={() => setOpenWheelDialog(false)} // Cho ph√©p ƒë√≥ng n·∫øu c·∫ßn
                aria-labelledby="wheel-of-food"
                sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}
            >
                {/* Box n√†y ch·ªâ ƒë·ªÉ ch·ª©a Roulette, kh√¥ng c·∫ßn style ph·ª©c t·∫°p */}
                <Box>
                    <Roulette
                        prizes={prizes}
                        start={mustSpin}
                        winningPrize={prizeNumber}
                        onPrizeSelected={handleSpinStop}
                        options={{
                            stopInCenter: true,
                        }}
                        // T√πy ch·ªânh m√†u s·∫Øc
                        designOptions={{
                            prizeItemWidth: 120,
                            prizeItemHeight: 120,
                            prizesWithText: true,
                            prizeItemColors: [
                            '#FF6347', '#FFD700', '#ADFF2F', '#87CEEB', '#BA55D3', 
                            '#FF4500', '#DAA520', '#3CB371', '#4169E1', '#9370DB'
                        ]}}
                    />
                </Box>
            </Modal>


            {/* Dialog hi·ªÉn th·ªã k·∫øt qu·∫£ */}
            <Dialog open={openResultDialog} onClose={handleCloseDialog}>
                <DialogTitle>Ch√∫c m·ª´ng!</DialogTitle>
                <DialogContent>
                    <Typography variant="h5" align="center" sx={{ my: 2 }}>
                        M√≥n ƒÉn ƒë∆∞·ª£c ch·ªçn l√†: <br />
                        <Box component="span" sx={{ fontWeight: 'bold', color: 'primary.main' }}>{finalPrize}</Box>
                    </Typography>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog} color="primary" autoFocus>
                        ƒê√≥ng
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default SpinWheel;
