import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import { Mail, Lock, LogIn, UserPlus, Home, Info, Map, Phone, Loader2, User, LogOut, ArrowLeft, Search } from 'lucide-react';

const Dashboard = ({ onLogout }) => {
    // Dữ liệu giả lập cho các món ăn
    const mockDishes = [
        { id: 1, name: "Phở Gà", likes: 120, date: "24 Tháng 10, 2024", comment: "Bát phở thơm ngon tuyệt vời!" },
        { id: 2, name: "Bún Chả", likes: 85, date: "15 Tháng 10, 2024", comment: "Nước chấm đậm đà, chả nướng vừa tới." },
        { id: 3, name: "Bánh Mì", likes: 150, date: "01 Tháng 10, 2024", comment: "Bánh mì giòn rụm, nhân đầy đủ." },
    ];

    const DishCard = ({ dish }) => (
        <div className="bg-white rounded-lg shadow-xl overflow-hidden transform hover:scale-[1.01] transition duration-300">
            {/* 10. Ảnh món ăn */}
            <div className="h-48 bg-gray-200 flex items-center justify-center text-gray-500 text-sm">
                [Hình ảnh Món ăn]
            </div>
            <div className="p-4">
                {/* 11. Tên món ăn */}
                <h3 className="text-lg font-semibold text-gray-900 mb-2">{dish.name}</h3>

                {/* 12. Nút Xem chi tiết */}
                <button className="text-sm text-indigo-600 font-medium hover:text-indigo-800 transition duration-150 flex items-center mb-4">
                    Xem chi tiết <ArrowLeft className="w-4 h-4 ml-1 rotate-180" />
                </button>

                {/* 13. Khu vực bình luận giả lập */}
                <div className="text-sm border-t pt-3 space-y-2">
                    <p className="font-medium text-gray-700">Bình luận</p>
                    <div className="flex items-center space-x-2">
                        <User className="w-5 h-5 text-indigo-500 p-0.5 rounded-full bg-indigo-100" />
                        <div>
                            <p className="font-semibold text-gray-800 text-xs">A SƠN</p>
                            <p className="text-gray-500 text-xs">{dish.date}</p>
                        </div>
                    </div>
                    <p className="text-gray-600 text-sm mt-1">{dish.comment}</p>
                    <div className="flex space-x-3 text-xs text-gray-500 mt-2">
                        <span>Phản hồi</span>
                        <span>|</span>
                        <span>Thích ({dish.likes})</span>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col items-center">

            {/* Header / Navigation */}
            <header className="w-full bg-gray-800 shadow-xl fixed top-0 z-50">
                <div className="flex justify-between items-center py-4 px-6 max-w-7xl mx-auto">
                    {/* 1. Logo */}
                    <div className="font-bold text-2xl text-white">LOGO</div>

                    {/* Navigation (2, 3, 4, 5) */}
                    <nav className="hidden md:flex space-x-8">
                        <a href="#" className="flex items-center text-white hover:text-indigo-400 transition duration-150"><Home className="w-4 h-4 mr-1"/>Trang chủ</a> {/* 2. Về trang chủ */}
                        <a href="#" className="flex items-center text-white hover:text-indigo-400 transition duration-150"><Info className="w-4 h-4 mr-1"/>Giới thiệu</a> {/* 3. Giới thiệu */}
                        <a href="#" className="flex items-center text-white hover:text-indigo-400 transition duration-150"><Map className="w-4 h-4 mr-1"/>Bản đồ</a> {/* 4. Bản đồ */}
                        <a href="#" className="flex items-center text-white hover:text-indigo-400 transition duration-150"><Phone className="w-4 h-4 mr-1"/>Liên hệ</a> {/* 5. Liên hệ */}
                    </nav>

                    {/* 6. Nút Đăng xuất */}
                    <button 
                        onClick={onLogout}
                        className="flex items-center px-3 py-2 border border-transparent rounded-lg text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 transition duration-150 shadow-md"
                    >
                        <LogOut className="w-4 h-4 mr-2"/>Đăng xuất
                    </button>
                </div>
            </header>

            {/* Main Content Area */}
            <div className="pt-24 pb-12 w-full max-w-7xl px-6">

                {/* Search Bar Area */}
                <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4 mb-10">

                    {/* 7. Nhập Tên thành phố/khu vực */}
                    <div className="relative flex-1">
