// --- 2. Registration Form Component ---

const RegistrationForm = ({ onRegistrationSuccess, onGoHome }) => {
    // Ẩn/Hiện form sau khi đăng ký thành công
    const [verificationLinkSent, setVerificationLinkSent] = useState(false);

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        confirmPassword: '',
    });
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    // State quản lý lỗi từng trường
    const [errors, setErrors] = useState({});

    // Regex cơ bản để kiểm tra định dạng email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    // Hàm kiểm tra lỗi
    const validateForm = () => {
        let newErrors = {};
        let isValid = true;

        // Kiểm tra Email
        if (!emailRegex.test(formData.email)) {
            newErrors.email = 'Email không hợp lệ. Vui lòng kiểm tra định dạng.';
            isValid = false;
        }

        // Kiểm tra Mật khẩu
        if (formData.password.length < 6) {
            newErrors.password = 'Mật khẩu phải có ít nhất 6 ký tự.';
            isValid = false;
        }

        // Kiểm tra Xác nhận Mật khẩu
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = 'Mật khẩu xác nhận không khớp.';
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };


    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
        // Xóa lỗi khi người dùng bắt đầu nhập lại
        if (errors[e.target.name]) {
            setErrors({ ...errors, [e.target.name]: null });
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setError(null);

        if (!validateForm()) {
            return;
        }

        setIsLoading(true);

        try {
            // GỌI API BACKEND: /api/register
            const response = await fetch(`${API_URL}/register`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: formData.email,
                    password: formData.password
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                // Xử lý lỗi từ server Node.js
                setError(data.message || 'Đăng ký thất bại. Vui lòng thử lại.');
                return;
            }

            // Đăng ký thành công -> Hiển thị thông báo gửi link xác thực
            setVerificationLinkSent(true);
            onRegistrationSuccess(); // Chuyển trang (nếu cần)

            // Log giả lập token để xem (Chỉ dùng cho mục đích phát triển)
            console.log('--- GIẢ LẬP TOKEN GỬI QUA EMAIL ---');
            console.log(`Token: ${data.token || 'a-dummy-jwt-token'}`);
            console.log(`Link xác thực: ${API_URL}/verify?token=${data.token || 'a-dummy-jwt-token'}`);

        } catch (err) {
            setError('Không thể kết nối tới Server Backend. Vui lòng kiểm tra Node.js server.');
        } finally {
            setIsLoading(false);
        }
    };

    if (verificationLinkSent) {
        return (
            <div className="max-w-md w-full p-6 space-y-8 bg-white rounded-xl shadow-2xl transition duration-500">
                <h2 className="mt-6 text-center text-3xl font-extrabold text-green-600">
                    <Mail className="w-8 h-8 inline-block mr-2" />
                    Đã Gửi Email Xác Thực
                </h2>
                <p className="text-center text-gray-600">
                    Một liên kết xác thực đã được gửi đến địa chỉ email của bạn. Vui lòng kiểm tra hộp thư và nhấp vào liên kết để kích hoạt tài khoản.
                </p>
                <div className="text-center">
                    <button
                        onClick={onGoHome}
                        className="w-full flex justify-center py-3 px-4 border border-transparent rounded-lg shadow-lg text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 transform hover:scale-[1.01]"
                    >
                        Đến trang Đăng nhập
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="max-w-md w-full p-6 space-y-8 bg-white rounded-xl shadow-2xl transition duration-500">
            <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900 flex items-center justify-center">
                <UserPlus className="w-8 h-8 text-indigo-600 mr-3" />
                Đăng Ký Tài Khoản Mới
            </h2>

            <form className="mt-8 space-y-6" onSubmit={handleRegister}>
                {error && (
                    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative text-sm" role="alert">
                        {error}
                    </div>
                )}

                {/* Trường Email */}
                <InputField
                    label="Địa chỉ Email"
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="vd: tenban@email.com"
                    icon={Mail}
                    isInvalid={!!errors.email}
                    errorText={errors.email}
                />

                {/* Trường Mật khẩu */}
                <InputField
                    label="Mật khẩu (Tối thiểu 6 ký tự)"
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Nhập mật khẩu"
                    icon={Lock}
                    isInvalid={!!errors.password}
                    errorText={errors.password}
                />

                {/* Trường Xác nhận Mật khẩu */}
                <InputField
                    label="Xác nhận Mật khẩu"
                    type="password"
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    placeholder="Nhập lại mật khẩu"
                    icon={Lock}
                    isInvalid={!!errors.confirmPassword}
                    errorText={errors.confirmPassword}
                />

                {/* Nút Đăng ký */}
                <div>
                    <button
                        type="submit"
                        disabled={isLoading}
                        className="w-full flex justify-center py-3 px-4 border border-transparent rounded-lg shadow-lg text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 transform hover:scale-[1.01] disabled:bg-indigo-400"
                    >
                        {isLoading ? <Loader2 className="w-5 h-5 animate-spin mr-2" /> : <UserPlus className="w-5 h-5 mr-2" />}
                        {isLoading ? 'Đang xử lý...' : 'Đăng Ký'}
                    </button>
                </div>

                {/* Liên kết Quay lại Trang chủ */}
                <div className="flex justify-center pt-4">
                    <button onClick={onGoHome} type="button" className="text-sm font-medium text-gray-500 hover:text-gray-700 transition duration-150">
                        <ArrowLeft className="w-4 h-4 inline-block mr-1"/> Quay lại Trang Chủ
                    </button>
                </div>
            </form>
        </div>
    );
};


// --- 3. Login Form Component ---

const LoginForm = ({ onLoginSuccess, onGoRegister, onGoHome }) => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        remember: false,
    });
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [errors, setErrors] = useState({});

    // Regex cơ bản để kiểm tra định dạng email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    const validateForm = () => {
        let newErrors = {};
        let isValid = true;

        if (!emailRegex.test(formData.email)) {
            newErrors.email = 'Email không hợp lệ.';
            isValid = false;
        }

        if (formData.password.length === 0) {
            newErrors.password = 'Mật khẩu không được để trống.';
            isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    };

    const handleChange = (e) => {
        const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
        setFormData({
            ...formData,
            [e.target.name]: value,
        });
        if (errors[e.target.name]) {
            setErrors({ ...errors, [e.target.name]: null });
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(null);

        if (!validateForm()) {
            return;
        }

        setIsLoading(true);

        try {
            // GỌI API BACKEND: /api/login
            const response = await fetch(`${API_URL}/login`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: formData.email,
                    password: formData.password
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                // Xử lý lỗi từ server Node.js
                setError(data.message || 'Đăng nhập thất bại. Vui lòng kiểm tra tài khoản/mật khẩu.');
                return;
            }

            // Đăng nhập thành công
            const token = data.token; // Lấy JWT token từ server
            localStorage.setItem('authToken', token);

            // Giả lập độ trễ chuyển hướng
            setTimeout(() => {
                 onLoginSuccess();
            }, 1500);

        } catch (err) {
            setError('Không thể kết nối tới Server Backend. Vui lòng kiểm tra Node.js server.');
        } finally {
            // Lưu ý: isLoading sẽ được tắt sau khi chuyển hướng trong setTimeout
        }
    };

    return (
        <div className="max-w-md w-full p-6 space-y-8 bg-white rounded-xl shadow-2xl transition duration-500">
            <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900 flex items-center justify-center">
                <LogIn className="w-8 h-8 text-indigo-600 mr-3" />
                Đăng Nhập Tài Khoản
            </h2>

            <form className="mt-8 space-y-6" onSubmit={handleLogin}>
                 {error && (
                    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative text-sm" role="alert">
                        {error}
                    </div>
                )}

                {/* 2. Trường Tên người dùng / Email */}
                <InputField
                    label="Địa chỉ Email"
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="vd: tenban@email.com"
                    icon={Mail}
                    isInvalid={!!errors.email}
                    errorText={errors.email}
                />

                {/* 3. Trường Mật khẩu */}
                <InputField
                    label="Mật khẩu"
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Nhập mật khẩu"
                    icon={Lock}
                    isInvalid={!!errors.password}
                    errorText={errors.password}
                />

                <div className="flex items-center justify-between">
                    {/* 4. Ghi nhớ tài khoản */}
                    <div className="flex items-center">
                        <input
                            id="remember"
                            name="remember"
                            type="checkbox"
                            checked={formData.remember}
                            onChange={handleChange}
                            className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                        />
                        <label htmlFor="remember" className="ml-2 block text-sm text-gray-900">
                            Ghi nhớ tài khoản
                        </label>
                    </div>

                    <div className="text-sm">
                        <a href="#" className="font-medium text-indigo-600 hover:text-indigo-500">
                            Quên mật khẩu?
                        </a>
                    </div>
                </div>

                <div className="flex space-x-4">
                    {/* 5. Nút Đăng nhập */}
                    <button
                        type="submit"
                        disabled={isLoading}
                        className="flex-1 flex justify-center items-center py-3 px-4 border border-transparent rounded-lg shadow-lg text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 transform hover:scale-[1.01] disabled:bg-indigo-400"
                    >
                        {isLoading ? <Loader2 className="w-5 h-5 animate-spin mr-2" /> : <LogIn className="w-5 h-5 mr-2" />}
                        {isLoading ? 'Đang đăng nhập...' : 'Đăng Nhập'}
                    </button>

                    {/* 6. Nút Tạo tài khoản mới */}
                    <button
                        type="button"
                        onClick={onGoRegister}
                        className="flex-1 flex justify-center items-center py-3 px-4 border border-indigo-600 rounded-lg shadow-lg text-sm font-medium text-indigo-600 bg-white hover:bg-indigo-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 transform hover:scale-[1.01]"
                    >
                        <UserPlus className="w-5 h-5 mr-2" />Tạo tài khoản mới
                    </button>
                </div>
            </form>
            {/* Liên kết Quay lại Trang chủ */}
            <div className="flex justify-center pt-4">
                <button onClick={onGoHome} type="button" className="text-sm font-medium text-gray-500 hover:text-gray-700 transition duration-150">
                    <ArrowLeft className="w-4 h-4 inline-block mr-1"/> Quay lại Trang Chủ
                </button>
            </div>
        </div>
    );
};


