// --- 5. Main App Component ---

const App = () => {
    // ĐẶT TRẠNG THÁI MẶC ĐỊNH ĐỂ XEM DASHBOARD (login = true)
    const [currentPage, setCurrentPage] = useState('dashboard'); 
    const [isLoggedIn, setIsLoggedIn] = useState(true); 

    const handleLoginSuccess = () => {
        setIsLoggedIn(true);
        setCurrentPage('dashboard');
    };

    const handleLogout = () => {
        localStorage.removeItem('authToken'); // Xóa token
        setIsLoggedIn(false);
        setCurrentPage('welcome');
    };

    // Nếu đã đăng nhập, luôn hiển thị Dashboard
    if (isLoggedIn) {
        return <Dashboard onLogout={handleLogout} />;
    }

    // Nếu chưa đăng nhập, hiển thị các trang Auth
    const renderPage = () => {
        switch (currentPage) {
            case 'login':
                return (
                    <LoginForm 
                        onLoginSuccess={handleLoginSuccess}
                        onGoRegister={() => setCurrentPage('register')}
                        onGoHome={() => setCurrentPage('welcome')}
                    />
                );
            case 'register':
                return (
                    <RegistrationForm 
                        onRegistrationSuccess={() => setCurrentPage('login')}
                        onGoHome={() => setCurrentPage('welcome')}
                    />
                );
            case 'welcome':
            default:
                return (
                    <WelcomePage 
                        onGoLogin={() => setCurrentPage('login')} 
                        onGoRegister={() => setCurrentPage('register')}
                    />
                );
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col justify-center items-center py-10">
            <div className="w-full">
                {renderPage()}
            </div>
        </div>
    );
};

export default App;
