import { LogIn, UserPlus } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

function App() {
  const navigate = useNavigate();
  // Các hàm xử lý sự kiện click, sau này sẽ dùng để chuyển trang
  const handleLoginClick = () => {
    navigate('/Login');
    navigate('/login');
  };

  const handleRegisterClick = () => {
    navigate('/register');
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col p-4 font-sans">
      <header className="w-full bg-white shadow-md rounded-lg p-4 flex justify-between items-center max-w-7xl mx-auto mt-4">
        <div className="font-bold text-2xl text-indigo-600">LOGO</div>

        <nav className="flex space-x-4">
          <button onClick={handleLoginClick} className="flex items-center px-4 py-2 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 hover:bg-gray-50 transition duration-150 shadow-sm">
            <LogIn className="w-4 h-4 mr-2" />Đăng nhập
          </button>
          <button onClick={handleRegisterClick} className="flex items-center px-4 py-2 border border-transparent rounded-lg text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 transition duration-150 shadow-md">
            <UserPlus className="w-4 h-4 mr-2" />Đăng ký
          </button>
        </nav>
      </header>

      <main className="flex-grow flex flex-col items-center justify-center text-center p-8">
        <h1 className="text-5xl md:text-6xl font-extrabold text-gray-900 mb-6">TÊN CHƯƠG TRÌNH</h1>
        <p className="text-lg md:text-xl text-gray-600 max-w-3xl mb-10">
          Chào mừng bạn đến với ứng dụng của chúng tôi. Hãy đăng ký để bắt đầu trải nghiệm những giải pháp tuyệt vời!
        </p>
        <button onClick={handleRegisterClick} className="px-8 py-4 text-lg font-semibold text-white bg-green-500 rounded-lg shadow-xl hover:bg-green-600 transition duration-200 transform hover:scale-105">
          Khám Phá Ngay
        </button>
      </main>

      <footer className="w-full bg-white text-center p-4 text-sm text-gray-500 shadow-inner rounded-lg max-w-7xl mx-auto mb-4">
        &copy; 2024 Tên Chương Trình. Bảo lưu mọi quyền.
      </footer>
    </div>
  );
}

export default App;
