import React, { useState } from 'react';
import { Box, Button, Typography, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import Roulette from 'react-roulette-pro'; 
import FastfoodIcon from '@mui/icons-material/Fastfood'; 
import 'react-roulette-pro/dist/index.css'; 

// --- DỮ LIỆU CÁC MÓN ĂN ---
const FOOD_OPTIONS = [
    'Phở Bò', 'Bún Chả', 'Nem Rán', 'Bánh Mì', 'Gỏi Cuốn', 
    'Cơm Tấm', 'Mì Quảng', 'Hủ Tiếu', 'Bún Bò Huế', 'Cháo Lòng',
    'Bánh Xèo', 'Cao Lầu', 'Bún Đậu Mắm Tôm', 'Phở Cuốn', 'Bánh Gật Gù'
];

// Chuyển đổi dữ liệu cho react-roulette-pro
// Đảm bảo có đủ dữ liệu để vòng quay tròn và có text
const prizes = FOOD_OPTIONS.map((food, index) => ({
    id: index,
    text: food,
    // Bạn có thể thêm một hình ảnh placeholder ở đây
    // image: 'https://via.placeholder.com/60', // Ví dụ ảnh nhỏ
}));

// --- COMPONENT SPIN WHEEL ---
const SpinWheel = ({ onFoodSelected }) => {
    const [openResultDialog, setOpenResultDialog] = useState(false);
    const [openWheelModal, setOpenWheelModal] = useState(false); 
    const [mustSpin, setMustSpin] = useState(false); 
    const [prizeNumber, setPrizeNumber] = useState(0); 
    const [finalPrize, setFinalPrize] = useState(''); 

    const handleSpinStop = (prize) => {
        const selectedFood = prizes[prizeNumber].text;
        
        setFinalPrize(selectedFood);
        setMustSpin(false); 
        setOpenWheelModal(false); 
        
        setTimeout(() => setOpenResultDialog(true), 300);

        if (onFoodSelected) {
            onFoodSelected(selectedFood);
        }
    };

    const handleSpin = () => {
        const randomIndex = Math.floor(Math.random() * prizes.length);
        setPrizeNumber(randomIndex);
        
        setOpenWheelModal(true); 
        setTimeout(() => setMustSpin(true), 100); 
    };

    const handleCloseResultDialog = () => {
        setOpenResultDialog(false);
        setFinalPrize('');
    };

    return (
        <Box sx={{ 
            position: 'fixed', 
            bottom: 30, 
            right: 30, 
            zIndex: 1000, 
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
        }}>
            {/* Nút nhỏ ở góc dưới bên phải */}
            <Button
                variant="contained"
                color="primary"
                size="large"
                sx={{
                    borderRadius: '50%', 
                    width: 80,
                    height: 80,
                    minWidth: 0, 
                    boxShadow: 6,
                    mb: 1, 
                    '&:hover': {
                        transform: 'scale(1.05)',
                        transition: 'transform 0.2s ease-in-out',
                    }
                }}
                onClick={handleSpin}
                aria-label="Quay để chọn món ăn"
            >
                <FastfoodIcon sx={{ fontSize: 40 }} />
            </Button>
            <Typography variant="caption" sx={{ color: 'text.secondary', fontWeight: 'bold' }}>Quay Món!</Typography>
            
            {/* Modal chứa vòng quay */}
            <Dialog
                open={openWheelModal}
                onClose={() => { 
                    // Chỉ cho phép đóng Modal khi vòng quay không đang quay
                    if (!mustSpin) {
                        setOpenWheelModal(false);
                    }
                }} 
                maxWidth="md" // Kích thước Modal phù hợp
                sx={{ 
                    '& .MuiDialog-paper': { 
                        p: 3, 
                        borderRadius: 2, 
                        textAlign: 'center',
                        backgroundColor: '#fefefe', // Nền Modal
                        border: '2px solid #ddd'
                    } 
                }}
            >
                <DialogTitle sx={{ p: 0, pb: 2 }}>
                    <Typography variant="h5" sx={{ fontWeight: 'bold', color: 'text.primary' }}>
                        運命のルーレット (Vòng quay định mệnh)
                    </Typography>
                </DialogTitle>
                <DialogContent sx={{ p: 0, display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column' }}>
                    <Roulette
                        prizes={prizes}
                        start={mustSpin}
                        winningPrize={prizeNumber}
                        onPrizeSelected={handleSpinStop} 
                        // Tùy chỉnh kích thước và thiết kế để có hình tròn và text
                        designOptions={{
                            prizeItemWidth: 100, // Chiều rộng mỗi phần
                            prizeItemHeight: 100, // Chiều cao mỗi phần
                            prizesWithText: true, // Hiển thị text
                            // prizeItemAlign: 'center', // Căn giữa text nếu cần
                            // prizeItemTextDirection: 'vertical', // Hướng text nếu cần
                            prizeItemColors: [
                                '#FF6347', '#FFD700', '#ADFF2F', '#87CEEB', '#BA55D3', 
                                '#FF4500', '#DAA520', '#3CB371', '#4169E1', '#9370DB',
                                '#F08080', '#DDA0DD', '#7B68EE', '#B0E0E6', '#FFDAB9'
                            ],
                            pointerColor: '#533c02',
                            pointerBackgroundColor: '#f7d057',
                            rouletteSize: 500, // Kích thước tổng thể của vòng quay
                            spinButtonText: '開始', // Text cho nút bên trong vòng quay
                            spinButtonColor: '#f7d057',
                            spinButtonTextColor: '#533c02',
                            hideSpinButton: true, // Ẩn nút Start bên trong vòng quay để dùng nút riêng
                        }}
                        options={{
                            stopInCenter: true,
                            // rotationDuration: 5000, // Thời gian quay (ms)
                            // animationEffect: 'easeOutSine',
                        }}
                    />
                    {/* Nút START riêng biệt bên dưới vòng quay */}
                    <Button
                        variant="contained"
                        color="primary"
                        size="large"
                        sx={{ mt: 3, width: 200, py: 1.5, fontSize: '1.2rem' }}
                        onClick={() => setMustSpin(true)} // Chỉ kích hoạt quay
                        disabled={mustSpin} // Vô hiệu hóa khi đang quay
                    >
                        スタート (START)
                    </Button>
                </DialogContent>
            </Dialog>

            {/* Dialog hiển thị kết quả */}
            <Dialog open={openResultDialog} onClose={handleCloseResultDialog}>
                <DialogTitle sx={{ textAlign: 'center' }}>Chúc mừng!</DialogTitle>
                <DialogContent>
                    <Typography variant="h5" align="center" sx={{ my: 2 }}>
                        Món ăn được chọn là: <br />
                        <Box component="span" sx={{ fontWeight: 'bold', color: 'primary.main' }}>{finalPrize}</Box>
                    </Typography>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseResultDialog} color="primary" autoFocus>
                        Đóng
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default SpinWheel;
