// src/index.js

import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

// Import các component mà chúng ta đã tạo
import HomePage from './pages/HomePage'; // Trang chủ
import Register from './pages/Register'; // Trang đăng ký
// Bạn sẽ cần tạo file LoginPage.jsx cho route /login
import LoginPage from './pages/LoginPage'; // Trang đăng nhập (giả định)


// Component App chứa logic định tuyến
const App = () => {
  return (
    // <Routes> bao bọc tất cả các định tuyến của bạn
    <Routes>
      {/* Route cho Trang chủ */}
      {/* Khi truy cập path "/", hiển thị component HomePage */}
      <Route path="/" element={<HomePage />} />
      
      {/* Route cho Trang Đăng ký */}
      {/* Khi truy cập path "/register", hiển thị component Register */}
      <Route path="/register" element={<Register />} />
      
      {/* Route cho Trang Đăng nhập */}
      {/* Khi truy cập path "/login", hiển thị component LoginPage */}
      <Route path="/login" element={<LoginPage />} />
      
      {/* Bạn có thể thêm các route khác nếu cần (ví dụ: Trang 404) */}
      <Route path="*" element={<h1>404 - Page Not Found</h1>} />
    </Routes>
  );
};

// Khởi tạo ứng dụng React
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    {/* BrowserRouter là component bao bọc cần thiết cho việc định tuyến */}
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </React.StrictMode>
);

// Lưu ý: Bạn cần đảm bảo đã cài đặt: npm install react-router-dom
