                component={RouterLink}
                to="/login"
                variant="outlined"
                sx={{ minWidth: 100 }}
              >
                „É≠„Ç∞„Ç§„É≥
              </Button>

              {/* 3. „É¨„Ç∏„Çπ„Çø„Éº (ƒêƒÉng k√Ω) */}
              <Button
                component={RouterLink}
                to="/register"
                variant="contained"
                color="primary"
                sx={{ minWidth: 100 }}
              >
                „É¨„Ç∏„Çπ„Çø„Éº
              </Button>
            </Box>
          </Toolbar>
        </AppBar>

        {/* === Main Content (V·ªã tr√≠ 4) === */}
        <Container component="main" sx={{ flexGrow: 1, py: 8, textAlign: 'center' }}>

          {/* 4. „Éó„É≠„Ç∞„É©„É†Âêç (T√™n ch∆∞∆°ng tr√¨nh) */}
