import React, { useState } from "react";
import {
  TextField,
  Button,
  Box,
  Typography,
  Checkbox,
  FormControlLabel,
} from "@mui/material";
import { Link } from "react-router-dom"; // Giả sử bạn dùng react-router-dom để chuyển hướng

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState(""); // Thêm state cho Nhập lại Mật khẩu
  const [agreeTerms, setAgreeTerms] = useState(false); // Thêm state cho Checkbox

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Thêm logic kiểm tra (ví dụ: mật khẩu khớp nhau)
    if (password !== confirmPassword) {
      alert("Mật khẩu và Xác nhận Mật khẩu không khớp!");
      return;
    }
    if (!agreeTerms) {
      alert("Bạn phải đồng ý với Điều khoản người dùng.");
      return;
    }

    try {
      console.log({ name, email, password, confirmPassword });
      // Logic gọi API đăng ký ở đây
    } catch (error) {
      console.error("Registration error:", error);
    }
  };

  return (
    <Box
      sx={{
        maxWidth: 400,
        margin: "auto",
        mt: 10,
        padding: 3,
        boxShadow: 3,
        borderRadius: 2,
        backgroundColor: "white", // Để nổi bật hơn
      }}
    >
      {/* 1. LOGO/Tiêu đề */}
      <Typography variant="h4" align="center" mb={1} color="primary">
        LOGO
      </Typography>
      <Typography variant="h5" align="center" mb={3}>
        Tạo Tài Khoản
      </Typography>

      <form onSubmit={handleSubmit}>
        {/* 2. Tên người dùng */}
        <TextField
          label="ユーザー名 (Tên người dùng)"
          variant="outlined"
          fullWidth
          margin="normal"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        {/* 3. Email */}
        <TextField
          label="メール (Email)"
          variant="outlined"
          fullWidth
          margin="normal"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {/* 4. Mật khẩu */}
        <TextField
          label="パスワード (Mật khẩu)"
          type="password"
          variant="outlined"
          fullWidth
          margin="normal"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {/* 5. Nhập lại Mật khẩu */}
        <TextField
          label="パスワードをもう一度入力してください (Nhập lại Mật khẩu)"
          type="password"
          variant="outlined"
          fullWidth
          margin="normal"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
        />

        {/* 6. Checkbox Điều khoản */}
        <FormControlLabel
          control={
            <Checkbox
              checked={agreeTerms}
              onChange={(e) => setAgreeTerms(e.target.checked)}
              name="agreeTerms"
              color="primary"
            />
          }
          label="ユーザー規約に同意する (Đồng ý với Điều khoản người dùng)"
          sx={{ my: 1 }}
        />

        {/* 7 & 8. Các nút hành động */}
        <Box sx={{ display: "flex", justifyContent: "space-between", mt: 3 }}>
          {/* 7. Nút Đăng ký (Tạo tài khoản) */}
          <Button
            type="submit"
            variant="contained"
            color="primary"
            disabled={!agreeTerms} // Vô hiệu hóa nếu chưa đồng ý điều khoản
            sx={{ width: "48%" }}
          >
            アカウントを作成する
          </Button>

          {/* 8. Nút Đăng nhập */}
          {/* Sử dụng Link để mô phỏng việc chuyển đến trang Login */}
          <Button
            component={Link} 
            to="/login" // Thay đổi đường dẫn thực tế của bạn
            variant="outlined"
            color="secondary"
            sx={{ width: "48%" }}
          >
            ログイン
          </Button>
        </Box>
      </form>

      {/* 9. Footer (Để mô phỏng vị trí) */}
      <Typography variant="body2" align="center" color="textSecondary" mt={5}>
        フッター (Footer content here)
      </Typography>
    </Box>
  );
};

export default Register;
