import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Login from './page/Login'; 
import Register from './page/Register'; 
import Dashboard from './page/Dashboard';
import Layout from './components/Layout'; 
import { AuthProvider } from './context/AuthContext';
import { AuthWrapper } from './context/AuthContext';
import Map from './page/Map'
import 'react-roulette-pro/dist/index.css';
// Component App ch·ª©a logic ƒë·ªãnh tuy·∫øn
const App = () => {
  return (
    <Routes>
      {/* C√°c route s·ª≠ d·ª•ng Layout chung (c√≥ Header v√† Footer) */}
      <Route path="/" element={<Layout />}>
        {/* index={true} c√≥ nghƒ©a l√† component n√†y s·∫Ω ƒë∆∞·ª£c render khi path kh·ªõp v·ªõi route cha ("/") */}
        <Route index element={<Dashboard />} />
        <Route path="/map" element={<Map />} />
        {/* Th√™m c√°c trang kh√°c c·∫ßn layout ·ªü ƒë√¢y, v√≠ d·ª•: */}
        
      </Route>
      
      {/* C√°c route kh√¥ng s·ª≠ d·ª•ng Layout (trang full-screen) */}
      <Route path="/register" element={<Register />} />
      <Route path="/login" element={<Login />} />
      {/* Route 404 */}
      <Route path="*" element={<h1>404 - Page Not Found</h1>} />
    </Routes>
  );
};

// Kh·ªüi t·∫°o ·ª©ng d·ª•ng Reac

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    {/* üõë AuthProvider ph·∫£i b·ªçc component ƒëang s·ª≠ d·ª•ng useContext (trong tr∆∞·ªùng h·ª£p n√†y l√† Login) */}
    <AuthProvider> 
      <BrowserRouter>
        <App />
      </BrowserRouter>
    </AuthProvider>
  </React.StrictMode>
);
