import React, { useContext, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthContext, AuthWrapper } from './context/AuthContext';
import { ProtectedRoute, PublicRoute } from './page/ProtectedRoutes.js';

import HomePage from './page/Homepage';
import Login from './page/Login';
import Register from './page/Register';
import Dashboard from './page/Dashboard';
import Layout from './components/Layout';
import MapComponent from './page/Map';

import api from './api/axios';

// Component để tự động đăng nhập lại khi tải lại trang
const AppInitializer = () => {
  const { auth, setAuth } = useContext(AuthContext);

  useEffect(() => {
    const checkUser = async () => {
      const token = localStorage.getItem('token');
      if (token && !auth.isAuthenticated) { // Chỉ chạy nếu có token và chưa được xác thực
        try {
          // Dùng token để lấy thông tin user
          const response = await api.get('/auth');
          setAuth({
            isAuthenticated: true,
            token: token,
            user: response.data,
          });
        } catch (error) {
          // Nếu token không hợp lệ, xóa nó đi
          localStorage.removeItem('token');
          setAuth({ isAuthenticated: false, user: null, token: null });
        }
      }
    };
    checkUser();
  }, [auth.isAuthenticated, setAuth]);

  return null; // Component này không render gì cả
};

function App() {
  return (
    <AuthWrapper>
      <Router>
        <AppInitializer />
        <Routes>
          {/* Các route công khai, ai cũng vào được */}
          <Route path="/homepage" element={<HomePage />} />

          {/* Các route chỉ người chưa đăng nhập mới vào được */}
          <Route element={<PublicRoute />}>
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
          </Route>

          {/* Các route chỉ người đã đăng nhập mới vào được */}
          <Route element={<ProtectedRoute />}>
            <Route element={<Layout />}>
              <Route path="/" element={<Dashboard />} />
              <Route path="/map" element={<MapComponent />} />
              {/* Thêm các trang được bảo vệ khác ở đây, ví dụ: /profile */}
            </Route>
          </Route>
        </Routes>
      </Router>
    </AuthWrapper>
  );
}

export default App;
