// src/index.js

import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

// Import các component từ thư mục './page/'
// SỬA: Homepage.jsx (H in hoa, p thường)
import Homepage from './page/Homepage'; // Trang chủ

// SỬA: Login.jsx (L in hoa)
import Login from './page/Login'; // Trang đăng nhập (Đã có file Login.jsx trong cấu trúc của bạn)

// Thêm import AuthProvider
import { AuthProvider } from './context/AuthContext';

// KHÔNG CẦN SỬA: Register.jsx (R in hoa)
import Register from './page/Register'; // Trang đăng ký


// Component App chứa logic định tuyến
const App = () => {
  return (
    <Routes>
      {/* Route cho Trang chủ */}
      {/* SỬA: Sử dụng component Homepage đã import */}
      <Route path="/" element={<Homepage />} />
      
      {/* Route cho Trang Đăng ký */}
      <Route path="/register" element={<Register />} />
      {/* Route cho Trang Đăng nhập */}
      {/* SỬA: Sử dụng component Login đã import */}
      <Route path="/login" element={<Login />} />
      
      {/* Route 404 */}
      <Route path="*" element={<h1>404 - Page Not Found</h1>} />
    </Routes>
  );
};

// Khởi tạo ứng dụng React
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
    </BrowserRouter>
    <AuthProvider>
      <BrowserRouter>
        <App />
      </BrowserRouter>
    </AuthProvider>
  </React.StrictMode>

  
);
